-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `number` char(32) NOT NULL,
  `bookname` char(32) NOT NULL,
  `author` char(32) NOT NULL,
  `price` float DEFAULT NULL,
  `authorinfo` varchar(1000) DEFAULT NULL,
  `contentinfo` varchar(1000) DEFAULT NULL,
  `category` char(32) DEFAULT NULL,
  `inventory` int(11) DEFAULT NULL,
  `sales` int(11) DEFAULT NULL,
  `date` date DEFAULT NULL,
  `keyword` char(32) DEFAULT NULL,
  `img1` varchar(128) DEFAULT NULL,
  `img2` varchar(128) DEFAULT NULL,
  `img3` varchar(128) DEFAULT NULL,
  `delsoft` int(11) DEFAULT '1',
  PRIMARY KEY (`number`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

