-- ----------------------------
-- Table structure for `shoppingcart`
-- ----------------------------
DROP TABLE IF EXISTS `shoppingcart`;
CREATE TABLE `shoppingcart` (
  `cartserial` int(11) NOT NULL AUTO_INCREMENT,
  `id` char(11) NOT NULL DEFAULT '' COMMENT '用户id',
  `number` char(32) NOT NULL COMMENT '书本序列号',
  `bookname` char(32) NOT NULL COMMENT '书本名字',
  `price` float NOT NULL COMMENT '书本价钱',
  `img1` varchar(128) NOT NULL,
  `count` int(11) NOT NULL COMMENT '书本数量',
  `totalprice` float NOT NULL COMMENT '总价',
  `delsoft` int(2) NOT NULL DEFAULT '1' COMMENT '软删除',
  `keyword` char(64) DEFAULT NULL COMMENT '订单编号关键词',
  PRIMARY KEY (`cartserial`),
  KEY `booknumber` (`number`),
  KEY `userid` (`id`),
  CONSTRAINT `booknumber` FOREIGN KEY (`number`) REFERENCES `book` (`number`),
  CONSTRAINT `userid` FOREIGN KEY (`id`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8;

