-- ----------------------------
-- View structure for `view_shoppingcart`
-- ----------------------------
DROP VIEW IF EXISTS `view_shoppingcart`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_shoppingcart` AS select `book`.`number` AS `booknumber`,`book`.`bookname` AS `bookname`,`book`.`price` AS `price`,`book`.`img1` AS `img1`,`shoppingcart`.`cartserial` AS `cartserial`,`shoppingcart`.`id` AS `id`,`shoppingcart`.`number` AS `number`,`shoppingcart`.`count` AS `count`,`shoppingcart`.`totalprice` AS `totalprice` from (`book` join `shoppingcart`) where (`shoppingcart`.`number` = `book`.`number`) ;
